mapper.select:
SELECT BRAND_KIND_CD , BRAND_NM , PRD_CNT , CODE_LEVEL , REG_DATE , REG_ID , MOD_DATE , MOD_ID , USE_YN FROM TF_BRAND WHERE 1=1
${item}
ORDER BY ${sort} ${direct}
ORDER BY BRAND_KIND_CD ASC
limit ${start}, ${size}

mapper.insert:
INSERT INTO TF_BRAND
    (BRAND_KIND_CD, BRAND_NM, CODE_LEVEL, USE_YN, REG_DATE, REG_ID)
VALUES(
    #{brandKindCd}, #{brandNm}, #{codeLevel}, #{useYn}, SYSDATE(), #{regId}
      )

mapper.update:
UPDATE TF_BRAND
SET
  BRAND_NM = #{brandNm}
  , CODE_LEVEL = #{codeLevel}
  , USE_YN = #{useYn}
  , MOD_ID = #{modId}
  , MOD_DATE = SYSDATE()
WHERE
    BRAND_KIND_CD = #{brandKindCd}
