mapper.insert:
INSERT INTO TF_COMM_CODE
    (COMM_CD, COMM_CD_NM, CODE_LEVEL, DISP_SEQ, COMMENT, USE_YN, REG_ID, REG_DATE)
VALUES(
    #{commCd,}, #{commCdNm}, #{codeLevel}, #{dispSeq}, #{comment}, #{useYn}, #{regId}, #{regDate}
)

mapper.update:
UPDATE TF_COMM_CODE
SET
    COMM_CD_NM = #{commCdNm}
  , CODE_LEVEL = #{codeLevel}
  , USE_YN = #{useYn}
  , MOD_ID = #{modId}
  , MOD_DATE = #{modDate}
WHERE
    COMM_CD = #{commCd}

UPDATE TF_COMM_CODE
SET
    USE_YN = #{useYn}
WHERE
    COMM_CD = #{commCd}
