mapper.select:
select STOCK_REG_SEQ, STOCK_STORE_CD, STOCK_STORE_NM, tp.BRAND_KIND_CD , stk.TF_PRD_CD,stk.TF_PRD_NM,TF_PRD_BARCODE ,PRD_SIZE,REAL_STOCK_CNT , RFID_STOCK_CNT, INVENTORY_CNT, stk.MOD_DATE from (select * from tf_stock ts where true
${item}
order by STOCK_REG_SEQ desc limit ${start}, ${size}) as stk left outer join tf_product tp on stk.TF_PRD_CD = tp.TF_PRD_CD

select STOCK_REG_SEQ, STOCK_STORE_CD, STOCK_STORE_NM, tp.BRAND_KIND_CD , stk.TF_PRD_CD,stk.TF_PRD_NM,TF_PRD_BARCODE ,PRD_SIZE,REAL_STOCK_CNT , RFID_STOCK_CNT, INVENTORY_CNT, stk.MOD_DATE from (select * from tf_stock ts where true
AND STOCK_STORE_CD = #{STORE_CD}
AND PRD_SIZE = #{PRD_SIZE}
order by STOCK_REG_SEQ desc ) as stk left outer join tf_product tp on stk.TF_PRD_CD = tp.TF_PRD_CD where true
AND BRAND_KIND_CD like '${BRAND_KIND_CD}%'
limit ${start}, ${size}

select count(*) from tf_stock_test
WHERE STOCK_STORE_CD = #{STOCK_STORE_CD}
AND TF_PRD_BARCODE= #{TF_PRD_BARCODE}

mapper.update:
UPDATE tf_stock_test
SET CUR_STOCK_CNT=${CUR_STOCK_CNT}, REAL_STOCK_CNT=${REAL_STOCK_CNT}, STD_STOCK_CNT=${STD_STOCK_CNT}, MOD_ID= #{userId} , MOD_DATE = now()
WHERE STOCK_STORE_CD = #{STOCK_STORE_CD}
AND TF_PRD_BARCODE= #{TF_PRD_BARCODE}
