mapper.select:
SELECT PER_MEMBER_NO , USER_ID , USER_NM , STORE_CD , USER_RANK_CD , USER_PHONE , USER_EMAIL , GRADE , REQ_JOIN_DATE , PDA_USE_YN , USER_STAT , REG_USER_ID FROM TF_USER WHERE 1=1
${item}
order by ${sort} ${direct}
order by PER_MEMBER_NO desc
limit ${start}, ${size}

mapper.update:
UPDATE TF_USER
SET USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}

UPDATE TF_USER
SET USER_NM = #{userNm}, STORE_CD=#{storeCd}, USER_RANK_CD=#{userRankCd}, USER_PHONE=#{userPhone}, USER_EMAIL=#{userEmail}, GRADE=#{grade}, PDA_USE_YN=#{pdaUseYn}, USER_STAT=#{userStat}
WHERE USER_ID = #{userId}

UPDATE TF_USER
SET USER_STAT = 'N'
WHERE PER_MEMBER_NO = ${perMemberNo}
